import React, { Component } from 'react'

// 类式组件
  class Demo extends Component {
    state={
        count:0
    }
    add=()=>{
        this.setState(state=>({count:state.count+1}))
    }
    componentDidMount(){
        setInterval(()=>{
            this.setState(state=>({count:state.count+1}))
        },1000)
    }
  render() {
    return (
      <div>
          <h2>当前求和为{this.state.count}</h2>
          <button onClick={this.add}>点我+1</button>
      </div>
    )
  }
}

