function Demo(){
    const [count,setCount] = React.useState(0)
    // console.log(count,setCount);
    // 加的回调
    function add(){
        setCount(count+1)
        // console.log('你点击了加号');
    }
    return (
               <div>
                  <h2>当前求和为{count}</h2>
                  <button onClick={add}>点我+1</button>
              </div>
             )
}
export default Demo
