function C(){
    return (
        <div className='grand'>
            <h3>我是C组件</h3>
            <h4>我从A组件接收到的用户名:
                <Consumer>
                    {
                        value =>{
                            return `${value.username},年龄是:${value.age}`
                        }
                    }
                </Consumer>    
            </h4>
        </div>
    

    )
}
