// 容器组件
// 引入count组件的UI组件
import CountUI from "../../components/Count";

// 引入redux中最为重要的store,需要在APP.js中引入，不能直接引入
// import store  from "../../redux/store";

// 引入connect用于连接UI组件与redux
import { connect } from "react-redux";

//connect是一个函数，它调用的返回值也是函数
// const CountContainer = connect()(CountUI)

// export default CountContainer

//a函数的返回值作为状态传递给了UI组件
//a函数返回的对象中的key就作为传递给UI组件props的key，value就作为传递给UI组件props的value----状态
function a(state){
    return {count:state}
}
//b函数返回的对象中的key就作为传递给UI组件props的key，value就作为传递给UI组件props的value----操作状态的方法
function b(){
    return {add:(number)=>{
        // 通知redux执行加法
        StorageEvent.dispatch({type:'increment',data:number})
    }}
}
//使用connect()()创建并暴露一个Count的容器组件
export default connect(a,b)(CountUI)
